import React, { useState, useRef, useEffect } from 'react';

const ChatbotWidget = () => {
  const [open, setOpen] = useState(false);
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([
    { role: 'bot', content: 'Welcome to ERNET India! How can I help you?' }
  ]);
  const chatEndRef = useRef(null);

  useEffect(() => {
    if (open && chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [chat, open]);

  const handleSend = () => {
    if (!message.trim()) return;
    setChat([...chat, { role: 'user', content: message }]);
    setMessage('');
    // Placeholder: Add bot response after user message
    setTimeout(() => {
      setChat(c => [...c, { role: 'bot', content: 'This is a placeholder response.' }]);
    }, 600);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  return (
    <div className="chatbot-widget-fixed">
      {open ? (
        <div className="chatbot-widget-box">
          <div className="chatbot-widget-header">
            <span>ERNET India</span>
            <button className="chatbot-widget-close" onClick={() => setOpen(false)}>&times;</button>
          </div>
          <div className="chatbot-widget-content">
            {chat.map((msg, idx) => (
              <div key={idx} className={`chatbot-msg chatbot-msg-${msg.role}`}>
                {msg.content}
              </div>
            ))}
            <div ref={chatEndRef} />
          </div>
          <div className="chatbot-widget-inputbar">
            <input
              className="chatbot-widget-input"
              type="text"
              placeholder="Type your message..."
              value={message}
              onChange={e => setMessage(e.target.value)}
              onKeyDown={handleKeyDown}
              autoFocus
            />
            <button className="chatbot-widget-submit" onClick={handleSend} disabled={!message.trim()}>
              Submit
            </button>
          </div>
        </div>
      ) : (
        <button className="chatbot-widget-open" onClick={() => setOpen(true)}>
          <span role="img" aria-label="chat">ðŸ’¬</span>
        </button>
      )}
    </div>
  );
};

export default ChatbotWidget;
