import React, { useState, useRef, useEffect } from 'react';

const ChatbotWidget = () => {
  const [open, setOpen] = useState(false);
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([
    { role: 'bot', content: 'Welcome to ERNET India! How can I help you?' }
  ]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    if (open && chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [chat, open]);

  const handleSend = async () => {
    if (!message.trim() || loading) return;
    const userMsg = { role: 'user', content: message };
    setChat([...chat, userMsg]);
    setMessage('');
    setLoading(true);
    try {
      const res = await fetch('/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message })
      });
      const data = await res.json();
      setChat(c => [...c, { role: 'bot', content: data.response || 'Sorry, no response.' }]);
    } catch (e) {
      setChat(c => [...c, { role: 'bot', content: 'Sorry, there was an error connecting to the server.' }]);
    }
    setLoading(false);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  return (
    <div className="chatbot-widget-fixed">
      {open ? (
        <div className="chatbot-widget-box">
          <div className="chatbot-widget-header">
            <span>ERNET India</span>
            <button className="chatbot-widget-close" onClick={() => setOpen(false)}>&times;</button>
          </div>
          <div className="chatbot-widget-content">
            {chat.map((msg, idx) => (
              <div key={idx} className={`chatbot-msg chatbot-msg-${msg.role}`}>
                {msg.content}
              </div>
            ))}
            {loading && <div className="chatbot-msg chatbot-msg-bot">...</div>}
            <div ref={chatEndRef} />
          </div>
          <div className="chatbot-widget-inputbar">
            <input
              className="chatbot-widget-input"
              type="text"
              placeholder="Type your message..."
              value={message}
              onChange={e => setMessage(e.target.value)}
              onKeyDown={handleKeyDown}
              autoFocus
              disabled={loading}
            />
            <button className="chatbot-widget-submit" onClick={handleSend} disabled={!message.trim() || loading}>
              Submit
            </button>
          </div>
        </div>
      ) : (
        <button className="chatbot-widget-open" onClick={() => setOpen(true)}>
          <span role="img" aria-label="chat">ðŸ’¬</span>
        </button>
      )}
    </div>
  );
};

export default ChatbotWidget;
